import React, { useState, useEffect } from 'react';
import './App.css';

const App = () => {
    const [formData, setFormData] = useState({
        name: "",
        state: "",
        city: "",
        hobbies: []
    });

    const [users, setUsers] = useState(() => {
        const savedUsers = localStorage.getItem('users');
        return savedUsers ? JSON.parse(savedUsers) : [];
    });

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleHobbiesChange = (e) => {
        const selectedHobbies = Array.from(e.target.selectedOptions, (option) => option.value);
        setFormData({
            ...formData,
            hobbies: selectedHobbies
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const newUser = { ...formData };
        console.log("Form Data Before Save:", newUser); // Verifique os dados antes de salvar

        setUsers((prevUsers) => {
            const updatedUsers = [...prevUsers, newUser];
            localStorage.setItem('users', JSON.stringify(updatedUsers));
            console.log("Updated Users:", updatedUsers); // Verifique os dados após salvar
            return updatedUsers;
        });

        // Resetar o formulário
        setFormData({ name: "", state: "", city: "", hobbies: [] });
    };

    useEffect(() => {
        console.log("Users Updated:", users); // Será chamado sempre que `users` for atualizado
        localStorage.setItem('users', JSON.stringify(users));
    }, [users]);

    document.addEventListener("DOMContentLoaded", () => {
        const form = document.getElementById("hobby-form");
        const userList = document.getElementById("users");
    
        // Função para carregar usuários salvos no localStorage
        const loadUsers = () => {
            const savedUsers = localStorage.getItem("users");
            return savedUsers ? JSON.parse(savedUsers) : [];
        };
    
        // Função para salvar usuários no localStorage
        const saveUsers = (users) => {
            localStorage.setItem("users", JSON.stringify(users));
        };
    
        // Função para renderizar a lista de usuários
        const renderUsers = () => {
            const users = loadUsers();
            userList.innerHTML = ""; // Limpa a lista atual
            users.forEach((user) => {
                const li = document.createElement("li");
                li.textContent = `${user.name} - ${user.city}, ${user.state} - Hobbies: ${user.hobbies.join(", ")}`;
                userList.appendChild(li);
            });
        };
    
        // Função para lidar com o envio do formulário
        form.addEventListener("submit", (e) => {
            e.preventDefault();
    
            const name = document.getElementById("name").value;
            const state = document.getElementById("state").value;
            const city = document.getElementById("city").value;
            const hobbies = Array.from(document.getElementById("hobbies").selectedOptions).map(
                (option) => option.value
            );
    
            // Cria um novo objeto de usuário
            const newUser = { name, state, city, hobbies };
    
            // Adiciona o novo usuário à lista e salva no localStorage
            const users = loadUsers();
            users.push(newUser);
            saveUsers(users);
    
            // Atualiza a lista de usuários na página
            renderUsers();
    
            // Reseta o formulário
            form.reset();
        });
    
        // Carrega e renderiza a lista de usuários ao carregar a página
        renderUsers();
    });
    
    return (
        <div className="App">
            <header>
                <div className="logo">
                    <a href="#home">
                        <img src="../public/logo.jpg" alt="Logo HobbySpot" />
                    </a>
                </div>
                <nav className="menu">
                    <ul>
                        <li><a href="#home">Home</a></li>
                        <li><a href="#about">Quem somos</a></li>
                        <li><a href="#contact">Contato</a></li>
                    </ul>
                </nav>
            </header>

            <main>
                <section id="form-section">
                    <h2>Cadastre-se para encontrar pessoas com hobbies semelhantes!</h2>
                    <form id="hobby-form" onSubmit={handleSubmit}>
                        <div className="input-group">
                            <label htmlFor="name">Nome</label>
                            <input
                                type="text"
                                id="name"
                                name="name"
                                placeholder="Digite seu nome"
                                value={formData.name}
                                onChange={handleInputChange}
                                required
                            />
                        </div>
                        <div className="input-group">
                            <label htmlFor="state">Estado</label>
                            <input
                                type="text"
                                id="state"
                                name="state"
                                placeholder="Digite o estado"
                                value={formData.state}
                                onChange={handleInputChange}
                                required
                            />
                        </div>
                        <div className="input-group">
                            <label htmlFor="city">Cidade</label>
                            <input
                                type="text"
                                id="city"
                                name="city"
                                placeholder="Digite a cidade"
                                value={formData.city}
                                onChange={handleInputChange}
                                required
                            />
                        </div>
                        <div className="input-group">
                            <label htmlFor="hobbies">Hobbies</label>
                            <select
                                id="hobbies"
                                name="hobbies"
                                multiple
                                value={formData.hobbies}
                                onChange={handleHobbiesChange}
                                required
                            >
                                <option value="musica">Música</option>
                                <option value="esportes">Esportes</option>
                                <option value="arte">Arte</option>
                                <option value="tecnologia">Tecnologia</option>
                            </select>
                        </div>
                        <button type="submit">Spot!</button>
                    </form>
                </section>

                <section id="user-list">
                    <h2>Lista Global de Usuários</h2>
                    <ul>
                        {users.map((user, index) => (
                            <li key={index}>
                                {user.name} - {user.city}, {user.state} - Hobbies: {user.hobbies.join(", ")}
                            </li>
                        ))}
                    </ul>
                </section>
            </main>

            <footer>
                <p>&copy; 2024 HobbySpot</p>
            </footer>
        </div>
    );
};

export default App;
