import React, { useState, useEffect } from 'react';
import './App.css';

const App = () => {
    const [statesAndCities] = useState({
        AC: ["Rio Branco", "Cruzeiro do Sul"],
        AL: ["Maceió", "Arapiraca"],
        SP: ["São Paulo", "Campinas", "Santos", "Ribeirão Preto", "Sorocaba"],
        RJ: ["Rio de Janeiro", "Niterói", "Petrópolis"],
    });

    const [formData, setFormData] = useState({
        name: "",
        state: "",
        city: "",
        hobbies: []
    });

    const [users, setUsers] = useState(() => {
        const savedUsers = localStorage.getItem('users');
        return savedUsers ? JSON.parse(savedUsers) : [];
    });

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleStateChange = (e) => {
        const selectedState = e.target.value;
        setFormData({
            ...formData,
            state: selectedState,
            city: "" // Reset city when state changes
        });
    };

    const handleHobbiesChange = (e) => {
        const selectedHobbies = Array.from(e.target.selectedOptions, (option) => option.value);
        setFormData({
            ...formData,
            hobbies: selectedHobbies
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        setUsers((prevUsers) => {
            const updatedUsers = [...prevUsers, formData];
            localStorage.setItem('users', JSON.stringify(updatedUsers));
            return updatedUsers;
        });
        setFormData({ name: "", state: "", city: "", hobbies: [] });
    };

    useEffect(() => {
        localStorage.setItem('users', JSON.stringify(users));
    }, [users]);

    return (
        <div className="App">
            <header>
                <div className="logo">
                    <a href="#home">
                        <img src="../assets/logo.png" alt="Logo HobbySpot" />
                    </a>
                </div>
                <nav className="menu">
                    <ul>
                        <li><a href="#home">Home</a></li>
                        <li><a href="#about">Quem somos</a></li>
                        <li><a href="#contact">Contato</a></li>
                    </ul>
                </nav>
            </header>

            <main>
                <section id="form-section">
                    <h2>Cadastre-se para encontrar pessoas com hobbies semelhantes!</h2>
                    <form id="hobby-form" onSubmit={handleSubmit}>
                        <div className="input-group">
                            <label htmlFor="name">Nome</label>
                            <input
                                type="text"
                                id="name"
                                name="name"
                                placeholder="Digite seu nome"
                                value={formData.name}
                                onChange={handleInputChange}
                                required
                            />
                        </div>
                        <div className="input-group">
                            <label htmlFor="state">Estado</label>
                            <select
                                id="state"
                                name="state"
                                value={formData.state}
                                onChange={handleStateChange}
                                required
                            >
                                <option value="" disabled>Selecione o estado</option>
                                {Object.keys(statesAndCities).map((state) => (
                                    <option key={state} value={state}>{state}</option>
                                ))}
                            </select>
                        </div>
                        <div className="input-group">
                            <label htmlFor="city">Cidade</label>
                            <select
                                id="city"
                                name="city"
                                value={formData.city}
                                onChange={handleInputChange}
                                required
                            >
                                <option value="" disabled>Selecione a cidade</option>
                                {(statesAndCities[formData.state] || []).map((city) => (
                                    <option key={city} value={city}>{city}</option>
                                ))}
                            </select>
                        </div>
                        <div className="input-group">
                            <label htmlFor="hobbies">Hobbies</label>
                            <select
                                id="hobbies"
                                name="hobbies"
                                multiple
                                value={formData.hobbies}
                                onChange={handleHobbiesChange}
                                required
                            >
                                <option value="musica">Música</option>
                                <option value="esportes">Esportes</option>
                                <option value="arte">Arte</option>
                                <option value="tecnologia">Tecnologia</option>
                            </select>
                        </div>
                        <button type="submit">Spot!</button>
                    </form>
                </section>

                <section id="user-list">
                    <h2>Lista Global de Usuários</h2>
                    <ul>
                        {users.map((user, index) => (
                            <li key={index}>
                                {user.name} - {user.city}, {user.state} - Hobbies: {user.hobbies.join(", ")}
                            </li>
                        ))}
                    </ul>
                </section>
            </main>

            <footer>
                <p>&copy; 2024 HobbySpot</p>
            </footer>
        </div>
    );
};

export default App;
