import React, { useState, useEffect } from 'react';
import './App.css';
import Form from './form';
import UserList from './UserList';

const App = () => {
    const [statesAndCities] = useState({
        AC: ["Rio Branco", "Cruzeiro do Sul"],
        AL: ["Maceió", "Arapiraca"],
        SP: ["São Paulo", "Campinas", "Santos", "Ribeirão Preto", "Sorocaba"],
        RJ: ["Rio de Janeiro", "Niterói", "Petrópolis"],
    });

    const [formData, setFormData] = useState({
        name: "",
        state: "",
        city: "",
        hobbies: []
    });

    const [users, setUsers] = useState(() => {
        const savedUsers = localStorage.getItem('users');
        return savedUsers ? JSON.parse(savedUsers) : [];
    });

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleStateChange = (e) => {
        const selectedState = e.target.value;
        setFormData({
            ...formData,
            state: selectedState,
            city: "" // Reset city when state changes
        });
    };

    const handleHobbiesChange = (e) => {
        const selectedHobbies = Array.from(e.target.selectedOptions, (option) => option.value);
        setFormData({
            ...formData,
            hobbies: selectedHobbies
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        setUsers((prevUsers) => {
            const updatedUsers = [...prevUsers, formData];
            localStorage.setItem('users', JSON.stringify(updatedUsers));
            return updatedUsers;
        });
        setFormData({ name: "", state: "", city: "", hobbies: [] });
    };

    useEffect(() => {
        localStorage.setItem('users', JSON.stringify(users));
    }, [users]);

    return (
        <div className="App">
            <header>
                <div className="logo"></div>
                <nav className="menu">
                    <a href="#home">Home</a>
                    <a href="#about">Quem somos</a>
                    <a href="#contact">Contato</a>
                </nav>
            </header>

            <main>
                <Form
                    formData={formData}
                    statesAndCities={statesAndCities}
                    handleInputChange={handleInputChange}
                    handleStateChange={handleStateChange}
                    handleHobbiesChange={handleHobbiesChange}
                    handleSubmit={handleSubmit}
                />
                <UserList users={users} />
            </main>

            <footer>
                <p>&copy; 2024 HobbySpot</p>
            </footer>
        </div>
    );
};

export default App;
